mapper.select:
SELECT
    cc.*,
    pcc.name AS parentName

${index} ${item}
 LIMIT #{offset},#{limit}

mapper.sql:
FROM sc_config_category cc
LEFT JOIN sc_config_category pcc ON cc.parent_id = pcc.id

    
        AND cc.id = #{id}
    
    
        AND cc.name LIKE CONCAT('%',#{name},'%')
    
    
        AND cc.key LIKE CONCAT('%',#{key},'%')
    
    
        AND cc.parent_id = #{parentId}
