mapper.select:
SELECT
    d.*,
    dc.name AS categoryName

${index} ${item}
 LIMIT #{offset},#{limit}

mapper.sql:
FROM sc_dictionary d
JOIN sc_dictionary_category dc ON d.category_key = dc.key

    
        AND d.id = #{id}
    
    
        AND d.value LIKE CONCAT('%',#{value},'%')
    
    
        AND d.key LIKE CONCAT('%',#{key},'%')
    
    
        AND d.lock = #{lock}
    
    
        AND d.status = #{status}
    
    
        AND dc.id = #{categoryId}
